mapper.update:
UPDATE measure_data
SET chest_pain      = #{chestPain},
    angina_pectoris = #{anginaPectoris}
WHERE person_id = #{memberId};

mapper.select:
SELECT family_id
FROM person
WHERE id = #{id}

SELECT name
FROM person
WHERE id = #{id}

SELECT car_prediction
FROM measure_data
WHERE person_id = #{id}

SELECT time
FROM appointment
WHERE person_id = #{id}

SELECT member_id, doctor_id, advice
FROM medical_advice
WHERE member_id = #{memberId}
